import { Component, Input, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { DeviceDetectorService } from 'src/app/device-detector.service';
import { Argument } from 'src/app/models/argument.model';

@Component({
  selector: 'app-arguments',
  templateUrl: './arguments.component.html',
  styleUrls: ['./arguments.component.scss']
})
export class ArgumentsComponent implements OnInit {

  //@Input() heights;
  isMobile: boolean;
  inputColor = 'red';
  arguments: Argument[];
  heights = {
    windowHeight: null,
    pageHeight: null
  }

  constructor(private activatedRoute: ActivatedRoute, private deviceDetector: DeviceDetectorService) { }

  ngOnInit() {
    window.scrollTo(0, 0);

    this.activatedRoute.data.subscribe((data: { arguments: Argument[] }) => {
      this.arguments = data.arguments; 
      console.log(this.arguments);
    })

    this.isMobile = this.deviceDetector.isMobile();
    console.log(window.innerHeight);

  }

  ngAfterViewInit() {

    this.heights.pageHeight = document.getElementById('xxx').offsetHeight;
    console.log(this.heights.pageHeight);

    if (this.heights.pageHeight > 10) {
      this.inputColor = 'blue';
    }

  }

}
