import { Component, OnInit, ViewEncapsulation } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Argument } from 'src/app/models/argument.model';
import { DeviceDetectorService } from 'src/app/device-detector.service';

@Component({
  selector: 'app-answer',
  templateUrl: './answer.component.html',
  styleUrls: ['./answer.component.scss'],
  encapsulation: ViewEncapsulation.None,
})

export class AnswerComponent implements OnInit {

  argument: Argument;
  answerID: string;
  index: number;
  isMobile: boolean;
  minimumHeight: string;

  type: string;

  answers: any[];
  backgroundColor: string;

  minHeight: string;
  argumentTitleColor: string;
  shortAnswerColor: string;
  textColor: string;
  copyIconSource: string;
  border: string;

  constructor(private router: Router, private activatedRoute: ActivatedRoute, private deviceDetector: DeviceDetectorService) { }

  ngOnInit() {
    window.scrollTo(0, 0);
    this.activatedRoute.data.subscribe((data: { argument: Argument }) => {
      this.argument = data.argument;
      this.type = this.argument.type;
    });
    this.isMobile = this.deviceDetector.isMobile();
    this.minimumHeight = this.deviceDetector.getMinimumHeight();
    this.getAnswerID();
    this.getSelectedAnswer();
    this.setStyles();
  }

  getAnswerID() {
    let url = this.router.url;
    let splits = url.split("/");
    this.answerID = splits[splits.length - 1];
  }

  getSelectedAnswer() {
    for (let i = 0; i < this.argument.answers.length; i++) {
     if (this.argument.answers[i]._id == this.answerID) {
      this.index = i;
     }
    }
  }

  setStyles() {
    this.type = this.argument.type;
    if (this.type == 'false' || (this.type == 'true')) {
      this.textColor = this.argumentTitleColor = this.shortAnswerColor = 'white';
      this.copyIconSource = "./../../../../assets/copy-white.svg";
      this.border = '1px solid white';

      if (this.type == 'false') {
        this.backgroundColor = 'rgba(164, 17, 17, 1)';
      }
      else {
        this.backgroundColor = 'rgba(17, 95, 164, 1)';
      }
    } else {
      this.argumentTitleColor = '#383838';
      this.textColor = this.shortAnswerColor = 'black';
      this.copyIconSource = "./../../../../assets/copy-black.svg";
      this.border = '1px solid black';
    }

  }

  editAnswer() {
    this.router.navigate(['/edit', this.argument._id, this.answerID]);
  }
}
