import { Component, OnInit, ViewEncapsulation } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { DeviceDetectorService } from 'src/app/device-detector.service';
import { Term } from 'src/app/models/term.model';

@Component({
  selector: 'app-answer',
  templateUrl: './term.component.html',
  styleUrls: ['./term.component.scss'],
  encapsulation: ViewEncapsulation.None,
})

export class TermComponent implements OnInit {

  term: Term;
  isMobile: boolean;

  constructor(private activatedRoute: ActivatedRoute, private deviceDetector: DeviceDetectorService) { }

  ngOnInit() {
    window.scrollTo(0, 0);
    this.activatedRoute.data.subscribe((data: { term: Term }) => {
      this.term = data.term;
    });
    this.isMobile = this.deviceDetector.isMobile();
  }
}
