import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Temp } from 'src/app/models/temp.model';
import { TachlesService } from 'src/app/tachles.service';

@Component({
  selector: 'app-new-argument-by-user',
  templateUrl: './new-argument-by-user.component.html',
  styleUrls: ['./new-argument-by-user.component.scss']
})
export class NewArgumentByUserComponent implements OnInit {

  titleInputCheck: string;
  fullInputCheck: string;
  shortInputCheck: string;

  constructor(private router: Router, private tachlesService: TachlesService) { }


  ngOnInit() {
  }

  submitNewArgument(title: string, short: string, full: string) {

    let tags: string[];
    let rank = 100;
    let newAnswer = {short, full, tags, rank};
    let type = 1;


      this.tachlesService.submitNewArgument(title, newAnswer, tags, type).subscribe((temp: Temp) => {
        this.router.navigate(['']);
      });
  }

  hideErrorMessage() {
  }
}
