import { Component, OnInit, ViewEncapsulation } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Argument } from 'src/app/models/argument.model';
import { DeviceDetectorService } from 'src/app/device-detector.service';

@Component({
  selector: 'app-answer',
  templateUrl: './answer.component.html',
  styleUrls: ['./answer.component.scss'],
  encapsulation: ViewEncapsulation.None,
})

export class AnswerComponent implements OnInit {

  argument: Argument;
  answerID: string;
  index: number;
  isMobile: boolean;

  constructor(private router: Router, private activatedRoute: ActivatedRoute, private deviceDetector: DeviceDetectorService) { }

  ngOnInit() {
    window.scrollTo(0, 0);
    this.activatedRoute.data.subscribe((data: { argument: Argument }) => {
      this.argument = data.argument;
    });
    this.isMobile = this.deviceDetector.isMobile();
    this.getAnswerID();
    this.getSelectedAnswer();
  }

  getAnswerID() {
    let url = this.router.url;
    let splits = url.split("/");
    this.answerID = splits[splits.length - 1];
  }

  getSelectedAnswer() {
    for (let i = 0; i < this.argument.answers.length; i++) {
     if (this.argument.answers[i]._id == this.answerID) {
      this.index = i;
     }
    }
  }

  editAnswer() {
    this.router.navigate(['/edit', this.argument._id, this.answerID]);
  }
}
