import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { WindowDetectorService } from 'src/app/window-detector.service';
import { Argument } from 'src/app/models/argument.model';

@Component({
  selector: 'app-argument',
  templateUrl: './argument.component.html',
  styleUrls: ['./argument.component.scss']
})

export class ArgumentComponent implements OnInit {

  type: string;
  argument: Argument;
  answers: any[];
  backgroundColor: string;
  isMobile: boolean;
  minimumHeight: string;
  argumentTitleColor: string;
  shortAnswerColor: string;
  textColor: string;
  copyIconSource: string;
  border: string;
  actionButtonColor: string;
  actionButtonBorder: string;

  constructor(private activatedRoute: ActivatedRoute, private windowDetector: WindowDetectorService) { }

  ngOnInit() {
    window.onbeforeunload = function() {window.scrollTo(0,0);}
    this.activatedRoute.data.subscribe((data: { argument: Argument }) => {
      this.argument = data.argument;
      this.answers = data.argument.answers;     
      this.type = this.argument.type;
    });
    this.setStyles();
    this.isMobile = this.windowDetector.isMobile();
    this.minimumHeight = this.windowDetector.getMinimumHeight();
  }

  setStyles() {
    if (this.type == 'false' || (this.type == 'true')) {
      this.actionButtonColor = this.textColor = this.argumentTitleColor = this.shortAnswerColor = 'white';
      this.copyIconSource = "./../../../../assets/copy-white.svg";
      this.border = '1px solid white';
      this.actionButtonBorder = '3px white solid';
      if (this.type == 'false') {
        this.backgroundColor = 'rgba(164, 17, 17, 1)';
      }
      else {
        this.backgroundColor = 'rgba(17, 95, 164, 1)';
      }
    } else {
      this.argumentTitleColor = '#383838';
      this.textColor = this.shortAnswerColor = 'black';
      this.copyIconSource = "./../../../../assets/copy-black.svg";
      this.border = '1px solid black';
      this.actionButtonColor = 'var(--variable-collection-gray-700)';
      this.actionButtonBorder = '3px var(--variable-collection-gray-700) solid';
    }
  }

  copy(i: number) {
    navigator.clipboard.writeText(this.answers[i].short);
  }
}
<!DOCTYPE html>
<html dir="rtl">
  <body>
    <app-topper [type]="type"></app-topper>
    <div class="desktop-main-wrapper" *ngIf="!isMobile" [ngStyle]="{'background-color': backgroundColor, 'min-height': minimumHeight}">
      <div class="desktop-right-sidebar-wrapper">
      </div>
      <div class="desktop-main-div-wrapper">
        <p class="desktop-argument-title" [ngStyle]="{'color': argumentTitleColor}">"{{ argument.title }}"</p>
        <div class="short-answer-wrapper" *ngFor="let answer of answers; index as i">
          <p class="desktop-short-answer" [ngStyle]="{'color': textColor}">{{ answer.short }}</p>
          <div class="desktop-read-copy-wrapper">
            <a class="desktop-read-more" [ngStyle]="{'color': textColor}" [routerLink]="[answer._id]">קראו עוד ←</a>
            <div class="copy-wrapper" [ngStyle]="{'border': border}" (click)="copy(i)">
              <div class="copy-text" [ngStyle]="{'color': textColor}">העתק</div>
              <img class="copy-icon" src="{{copyIconSource}}" />
            </div>
          </div>
        </div>
        <div class="desktop-action-buttons-wrapper">
          <a class="desktop-action-button" routerLink="/new"
          [ngStyle]="{'color': actionButtonColor, 'border': actionButtonBorder}">הוספת טיעון</a>
          <a class="desktop-action-button" [routerLink]="['../../', 'new', argument._id]"
          [ngStyle]="{'color': actionButtonColor, 'border': actionButtonBorder}">הוספת תשובה</a>
        </div>
      </div>
      <div class="desktop-left-sidebar-wrapper">
      </div>
    </div>
    <div *ngIf="isMobile" [ngStyle]="{'background-color': backgroundColor}">
      <div class="mobile-main-wrapper" >
        <div class="mobile-argument-title" [ngStyle]="{'color': argumentTitleColor}">״אין פרטנר״</div>
        <div class="short-answer-wrapper">
          <p class="mobile-short-answer">
            השאלה &#34;האם יש פרטנר?&#34; היא במידה רבה חצי שאלה. השאלה המלאה צריכה להיות: &#34;האם יש פרטנר לתוכנית
            מסוימת?&#34;.<br />במילים אחרות, יש לשאול: האם יש תוכנית שהצד הישראלי מעוניין בה? האם התוכנית הזו
            זוכה לתמיכה של הקהילה הבינלאומית? ובמידה והתוכנית עונה לשני התנאים הראשונים, האם יש לה פרטנר בצד הפלסטיני?
          </p>
          <div class="mobile-read-copy-wrapper">
            <p class="mobile-read-more">קראו עוד ←</p>
            <div class="copy-secondary-wrapper" [ngStyle]="{'border': border}">
              <div class="copy-text">העתק</div>
              <img class="copy-icon" src="{{copyIconSource}}" />      
            </div>
          </div>
        </div>
      </div>
    </div>
    <app-footer></app-footer>
  </body>
</html>
