import { Component, OnInit } from '@angular/core';
import { DeviceDetectorService } from 'src/app/device-detector.service';
import { Heights } from 'src/app/models/heights';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {

  isMobile: boolean;
  box = document.getElementById("aaa");
  //windowHeight: number;
  //pageHeight: number;
  heights: Heights;
  /*
  heights = {
    windowHeight: null,
    pageHeight: null
  }
    */

  constructor(private deviceDetector: DeviceDetectorService) { }

  ngOnInit() {
    //this.dynu.y = 35;
    window.scrollTo(0, 0);
    this.isMobile = this.deviceDetector.isMobile();
   // console.log(document.getElementById('xxx').offsetHeight);
    console.log(window.innerWidth);
  //  console.log(this.box.offsetWidth);
    //this.heights.window = window.innerHeight;
  //  this.windowHeight = window.innerHeight;
  }


  ngAfterViewInit() {
    //this.pageHeight = document.getElementById('xxx').offsetHeight;
    //console.log(this);
    this.heights.page = document.getElementById('xxx').offsetHeight;
  }
  gg() {
   // console.log(this.box.offsetHeight);
  }
}
