import { Component, OnInit } from '@angular/core';
import { DeviceDetectorService } from 'src/app/device-detector.service';

@Component({
  selector: 'app-topper',
  templateUrl: './topper.component.html',
  styleUrls: ['./topper.component.scss']
})

export class TopperComponent implements OnInit {

  isMobile: boolean;
  xxx: string;
  backgroundColor: string;
  logoTitleColor: string;
  logoSubtitleColor: string;
  desktopTopperDivider: string;
  topperLink: string;
  inputColor: string;


  constructor(private deviceDetector: DeviceDetectorService) { }

  ngOnInit() {
    this.isMobile = this.deviceDetector.isMobile();
    this.getPromoStyles();
  }

  getPromoStyles() {
    if (window.location.href.indexOf("irr") > -1) {
      //return 'background-color: red'
      this.xxx = 'pink';
    }
    else if (window.location.href.indexOf("false") > -1) {
      /*
      //return 'background-color: white'
      this.backgroundColor = 'rgba(164, 17, 17, 1)';
      this.logoTitleColor = 'white';
      this.logoSubtitleColor = 'white';
      this.desktopTopperDivider = 'white';
      //this.topperLink = 'white';
      this.inputColor = this.topperLink = 'white';
      */
  }
  }
}
