import { Injectable } from '@angular/core';
import { Resolve, ActivatedRouteSnapshot } from '@angular/router';
import { Observable } from 'rxjs'; 
import { Argument } from 'src/app/models/argument.model';
//import { Argument}
import { TachlesService } from 'src/app/tachles.service';

@Injectable({
  providedIn: 'root'
})

export class ArgumentResolverService implements Resolve<Argument> {

  argumentID: string;

  constructor(private tachlesService: TachlesService) { }

  resolve(route: ActivatedRouteSnapshot): 
  Observable<any> | Promise<any> | Argument {
    this.argumentID = route.paramMap.get('argumentID');
    return this.tachlesService.getSelectedArgument(this.argumentID);
  }
}
