import { Component, OnInit } from '@angular/core';
import { AUTO_STYLE, animate, state, style, transition, trigger } from '@angular/animations';
import { Term } from 'src/app/models/term.model';
import { ActivatedRoute } from '@angular/router';
import { DeviceDetectorService } from 'src/app/device-detector.service';

const DEFAULT_DURATION = 300;

@Component({
  selector: 'app-dictionary',
  templateUrl: './dictionary.component.html',
  styleUrls: ['./dictionary.component.scss'],
  animations: [
    trigger('dropdown', [
      state('false', style({ height: AUTO_STYLE, visibility: AUTO_STYLE })),
      state('true', style({ height: '0', visibility: 'hidden' })),
      transition('false => true', animate(DEFAULT_DURATION + 'ms ease-in')),
      transition('true => false', animate(DEFAULT_DURATION + 'ms ease-out'))
    ])
  ]

})
export class DictionaryComponent implements OnInit {
  anoter: string[];
  terms: Term[];
  termDefinitionStatus: boolean[]; // TRUE if the term's definition is closed/invisible; FALSE if it's open/visible
  isMobile: boolean;
  //letters = ['א','ב','ג','ד','ה','ו','ז','ח','ט','כ','ל','מ','נ','ס','ע','פ','צ','ק','ר','ש','ת'];
  letters = [];

  constructor(private activatedRoute: ActivatedRoute, private deviceDetector: DeviceDetectorService) { }

  ngOnInit() {
    window.scrollTo(0, 0);
    this.activatedRoute.data.subscribe((data: { terms: Term[] }) => {
      this.terms = data.terms;
    })
    for (var i in this.terms) {
    //  console.log(this.terms[i].title[0]);
      if (!this.letters.includes(this.terms[i].title[0])) {
        this.letters.push(this.terms[i].title[0]);
      }
    }
    console.log(this.letters);
    this.anoter = ['mara', 'xara', 'ara'];
    this.isMobile = this.deviceDetector.isMobile();
    this.termDefinitionStatus = [];
    for (let i = 0; i < this.terms.length ; i++) {
