import { Component, OnInit } from '@angular/core';
import { TachlesService } from 'src/app/tachles.service';
import { ActivatedRoute, Router } from '@angular/router';
import { Argument } from 'src/app/models/argument.model';
import { Temp } from 'src/app/models/temp.model';

@Component({
  selector: 'app-new-answer-by-user',
  templateUrl: './new-answer-by-user.component.html',
  styleUrls: ['./new-answer-by-user.component.scss']
})

export class NewAnswerByUserComponent implements OnInit {

  title: string;
  argumentID: String;
  argument: Argument;
  id: string;
  index: number;

  constructor(private tachlesService: TachlesService, private router: Router, private activatedRoute: ActivatedRoute) { }

  ngOnInit() {
    window.scrollTo(0, 0);
    this.activatedRoute.data.subscribe((data: { argument: Argument }) => {
      this.argument = data.argument;
    });
    this.argumentID = this.argument._id;
    this.title = this.argument.title;
    this.getAnswerID();
  }

  getAnswerID() {
    let url = this.router.url;
    let splits = url.split("/");
    this.id = splits[splits.length - 1];
  }

  submitNewAnswer(short: string, full: string) {
    let tags: string[];
    let rank = 100;
    let newAnswer = {short, full, tags, rank};
    let type = 2;

    this.tachlesService.submitNewAnswer(this.argumentID, this.title, newAnswer, tags, type).subscribe((temp: Temp) => {
        this.router.navigate(['']);
      });
  }

  hideErrorMessage() {
  }
}
