import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DeviceDetectorService } from 'src/app/device-detector.service';
import { Argument } from 'src/app/models/argument.model';

@Component({
  selector: 'app-argument',
  templateUrl: './argument.component.html',
  styleUrls: ['./argument.component.scss']
})

export class ArgumentComponent implements OnInit {
  type: string;
  argument: Argument;
  answers: any[];
  backgroundColor: string;
  isMobile: boolean;
  minHeight: string;

  constructor(private router: Router, private activatedRoute: ActivatedRoute, private deviceDetector: DeviceDetectorService) { }

  ngOnInit() {
    window.scrollTo(0, 0);
    this.activatedRoute.data.subscribe((data: { argument: Argument }) => {
      this.argument = data.argument;
      this.answers = data.argument.answers;     
      this.type = this.argument.type;
      console.log(this.type);
  });
  this.isMobile = this.deviceDetector.isMobile();
  this.setStyles();
  this.setMinimumHeight();
}

setMinimumHeight() {
  this.minHeight = window.innerHeight - 107 - 81 - 30 + 'px';
}

setStyles() {
  if (this.type == 'false' || (window.location.href.indexOf("true") > -1)) {
    //return 'background-color: white'
/*    
    this.logoTitleColor = 'white';
    this.logoSubtitleColor = 'white';
    this.desktopTopperDivider = 'white';
    this.topperLinkColor = 'white';
    this.inputColor = this.topperLinkColor = 'white';
    this.searchIconColor = 'url(./../../../assets/search-white.svg)'; 
    console.log(window.innerHeight);
*/
    if (this.type == 'false') {
      this.backgroundColor = 'rgba(164, 17, 17, 1)';
    }
    else {
      this.backgroundColor = 'rgba(17, 95, 164, 1)';
    }
  }

}

  addAnswer() {
    this.router.navigate(['/new', this.argument._id]);
  }
}
<!DOCTYPE html>
<html dir="rtl">
  <body>

    <app-topper [type]="type"></app-topper>

    <div class="desktop-main-wrapper" *ngIf="!isMobile"  [ngStyle]="{'background-color': backgroundColor, 'min-height': minHeight}">
      <div class="desktop-right-sidebar-wrapper">
      </div>
      <div class="desktop-main-div-wrapper">
        <p class="desktop-headline">"{{ argument.title }}"</p>

        <div class="short-answer-wrapper" *ngFor="let answer of answers">
          <p class="short-answer">
            {{answer.short}}
            </p>
            <div class="buttons-wrapper">
              <p class="read-more" [routerLink]="[answer._id]">קראו עוד ←</p>
              <div class="copy-wrapper">
                <div class="copy-text">העתק</div>
                <img class="copy-icon" src="./../../../../assets/copy-white.svg" />
              </div>
            </div>
        </div>

<br>
<br>
<br>
<br>




      </div>
      <div class="desktop-left-sidebar-wrapper">
      </div>
    </div>
    <div class="false-argument" *ngIf="isMobile">
      <div class="mobile-main-wrapper">
        <div class="mobile-headline">״אין פרטנר״</div>
        <div class="short-answer-wrapper">
          <p class="mobile-short-answer">
            השאלה &#34;האם יש פרטנר?&#34; היא במידה רבה חצי שאלה. השאלה המלאה צריכה להיות: &#34;האם יש פרטנר לתוכנית
            מסוימת?&#34;.<br />במילים אחרות, יש לשאול: האם יש תוכנית שהצד הישראלי מעוניין בה? האם התוכנית הזו
            זוכה לתמיכה של הקהילה הבינלאומית? ובמידה והתוכנית עונה לשני התנאים הראשונים, האם יש לה פרטנר בצד הפלסטיני?
          </p>
          <div class="buttons-wrapper">
          <p class="mobile-read-more">קראו עוד ←</p>
          <div class="copy-wrapper">
            <div class="copy-text">העתק</div>
            <img class="copy-icon" src="./../../../../assets/copy-white.svg" />


          </div>
            </div>
        </div>

        <!--
