import { Component, OnInit } from '@angular/core';
import { DeviceDetectorService } from 'src/app/device-detector.service';

@Component({
  selector: 'app-topper',
  templateUrl: './topper.component.html',
  styleUrls: ['./topper.component.scss']
})

export class TopperComponent implements OnInit {

  isMobile: boolean;
  xxx: string;
  backgroundColor: string;
  logoTitleColor: string;
  logoSubtitleColor: string;
  desktopTopperDivider: string;
  topperLinkColor: string;
  inputColor: string;
  searchIconColor: string;


  constructor(private deviceDetector: DeviceDetectorService) { }

  ngOnInit() {
    this.isMobile = this.deviceDetector.isMobile();
    this.getPromoStyles();

  }

  getPromoStyles() {
    if (window.location.href.indexOf("false") > -1 || (window.location.href.indexOf("true") > -1)) {
      //return 'background-color: white'

      this.logoTitleColor = 'white';
      this.logoSubtitleColor = 'white';
      this.desktopTopperDivider = 'white';
      this.topperLinkColor = 'white';
      this.inputColor = this.topperLinkColor = 'white';
      this.searchIconColor = 'url(./../../../assets/search-white.svg)'; 

      if (window.location.href.indexOf("false") > -1) {
        this.backgroundColor = 'rgba(164, 17, 17, 1)';
      }
      else {
        this.backgroundColor = 'rgba(17, 95, 164, 1)';
      }
    }

  }

  ngAfterViewInit() {
    console.log(document.getElementById('topper-height').offsetHeight);
  }

  inputStyle(): Object {
    if (window.location.href.indexOf("false") > -1 || (window.location.href.indexOf("true") > -1)) {
        return {color: this.inputColor, background: 'url(./../../../assets/search-white.svg)', 'background-repeat': 'no-repeat',
          'background-position': '230px 9px' 
        }
    }
    return {}
}

  }
