import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { DeviceDetectorService } from 'src/app/device-detector.service';
import { Argument } from 'src/app/models/argument.model';

@Component({
  selector: 'app-irrefutable-arguments',
  templateUrl: './irrefutable-arguments.component.html',
  styleUrls: ['./irrefutable-arguments.component.scss']
})
export class IrrefutableArgumentsComponent {
  isMobile: boolean;
  arguments: Argument[];

  constructor(private activatedRoute: ActivatedRoute, private deviceDetector: DeviceDetectorService) { }

  ngOnInit() {
    window.scrollTo(0, 0);

    this.activatedRoute.data.subscribe((data: { arguments: Argument[] }) => {
      this.arguments = data.arguments; 
      console.log(this.arguments);
    })

    this.isMobile = this.deviceDetector.isMobile();
    console.log(window.innerHeight);

  }
}
