import { Injectable } from '@angular/core';
import { Resolve, ActivatedRouteSnapshot } from '@angular/router';
import { Observable } from 'rxjs'; 
import { TachlesService } from 'src/app/tachles.service';
import { Term } from 'src/app/models/term.model';

@Injectable({
  providedIn: 'root'
})

export class TermResolverService implements Resolve<Term> {

  termID: string;

  constructor(private tachlesService: TachlesService) { }

  resolve(route: ActivatedRouteSnapshot): 
  Observable<any> | Promise<any> | Term {
    this.termID = route.paramMap.get('termID');
    return this.tachlesService.getSelectedTerm(this.termID);
  }
}
